mapper.select:
        SELECT


--         级联表的字段

        FROM yaopin  a



                and a.id in

                #{item}



                and a.yaopin_name like CONCAT('%',#{params.yaopinName},'%')


                and a.yaopin_types = #{params.yaopinTypes}


                  and a.yaopin_kucun_number >= #{params.yaopinKucunNumberStart}   


                  and a.yaopin_kucun_number <= #{params.yaopinKucunNumberEnd}   


                and a.yaopin_kucun_number = #{params.yaopinKucunNumber}


                and a.danwei_types = #{params.danweiTypes}


                  and a.yaopin_new_money >= #{params.yaopinNewMoneyStart}   


                  and a.yaopin_new_money <= #{params.yaopinNewMoneyEnd}   


                and a.yaopin_content like CONCAT('%',#{params.yaopinContent},'%')


                  and a.yaopin_delete >= #{params.yaopinDeleteStart}   


                  and a.yaopin_delete <= #{params.yaopinDeleteEnd}   


                and a.yaopin_delete = #{params.yaopinDelete}




        order by a.${params.orderBy} desc
